package com.automan.siberia.observation.observation1;import com.google.common.collect.Lists;import java.util.List;/** * @Author he.zhou * @Date 2021-04-28 */public class WeChat1 implements Subject {    private String text;    private List<Member> memberList = Lists.newArrayList();    @Override    public void addMember(Member member) {        memberList.add(member);    }    @Override    public void deleteMember(Member member) {        memberList.remove(member);    }    @Override    public void notifyToMember() {        memberList.forEach(member -> {            member.receive(text);        });    }}