package com.automan.siberia.pruduceComsumer;import com.google.common.base.Splitter;import java.math.BigDecimal;import java.util.List;/** * @Author he.zhou * @Date 2020-12-21 */public class JijinTest {    public static void main(String[] args) {        String ss = "9985 2\n" +                "20278 1.86\n" +                "38185 0.4\n" +                "5212.84 1.87\n" +                "12360.08 2.42\n" +                "13066.56 0.51\n" +                "16573 1.08\n" +                "7142 0.03\n" +                "5185.59 4.08\n" +                "19837.61 4.4\n" +                "12317 4.39\n" +                "8893 1.99\n" +                "6512 2.22\n" +                "8153 1.78\n" +                "7812 4.32\n" +                "2926 3.34\n" +                "5115 1.87\n" +                "12727 3.01\n" +                "10632 1.13\n" +                "5428 4.86";        List<String> strings = Splitter.on("\n").splitToList(ss);        BigDecimal decimal = strings.stream().map(ar -> {            List<String> strings1 = Splitter.on(" ").splitToList(ar);            return new BigDecimal(strings1.get(0)).multiply(new BigDecimal(strings1.get(1)).divide(new BigDecimal(100)));        }).reduce(BigDecimal.ZERO, BigDecimal::add);        System.out.println(decimal);    }}