package com.automan.siberia.pruduceComsumer;import java.util.concurrent.locks.ReentrantLock;/** * @Author he.zhou * @Date 2020-09-17 */public class ReentrantLockTest {    private static ReentrantLock lock = new ReentrantLock(true);    public void getOne(String string) {        lock.lock();        try {            System.out.println("获取到锁" + "========" + string);            Thread.sleep(2000);        } catch (InterruptedException e) {            e.printStackTrace();        }        lock.unlock();    }    public static void main(String[] args) throws InterruptedException {        ReentrantLockTest test = new ReentrantLockTest();        new Thread(() -> test.getOne("1")).start();        new Thread(() -> test.getOne("2")).start();        Thread.sleep(3000);        test.getOne("4");    }}