package com.automan.siberia.sort;import com.automan.siberia.Node;import java.sql.SQLOutput;/** * @Author he.zhou * @Date 2020-11-12 */public class BearTest {    private static int erfen(int[] arr, int target) {        int start = 0;        int end = arr.length - 1;        while (start <= end) {            int mid = (start + end) / 2;            if (arr[mid] < target) {                start = mid + 1;            } else if (arr[mid] > target) {                end = mid - 1;            } else {                return mid;            }        }        return -1;    }    public static Node hebing(Node<Integer> node1, Node<Integer> node2) {        if (node1 == null) {            return node2;        }        if (node2 == null) {            return node1;        }        Node prev;        if (node1.getData() > node2.getData()) {            prev = node2;            prev.setNext(hebing(node1, node2.getNext()));        } else {            prev = node1;            prev.setNext(hebing(node1.getNext(), node2));        }        return prev;    }    public static void main(String[] args) {        int[] arr = new int[]{1, 2, 3, 4, 5, 6, 7};        System.out.println(erfen(arr, 7));    }}